import 'dart:io';

import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:flutter_svg/svg.dart';
import 'package:stackfrost/providers/providers.dart';
import 'package:stackfrost/themes/coin_icon_provider.dart';
import 'package:stackfrost/themes/stack_colors.dart';
import 'package:stackfrost/utilities/amount/amount_formatter.dart';
import 'package:stackfrost/utilities/constants.dart';
import 'package:stackfrost/utilities/enums/sync_type_enum.dart';
import 'package:stackfrost/utilities/text_styles.dart';
import 'package:stackfrost/utilities/util.dart';
import 'package:stackfrost/widgets/background.dart';
import 'package:stackfrost/widgets/conditional_parent.dart';
import 'package:stackfrost/widgets/custom_buttons/app_bar_icon_button.dart';
import 'package:stackfrost/widgets/custom_buttons/draggable_switch_button.dart';
import 'package:stackfrost/widgets/rounded_white_container.dart';

class WalletSyncingOptionsView extends ConsumerWidget {
  const WalletSyncingOptionsView({Key? key}) : super(key: key);

  static const String routeName = "/walletSyncingOptions";

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final managers = ref
        .watch(walletsChangeNotifierProvider.select((value) => value.managers));

    final isDesktop = Util.isDesktop;
    return ConditionalParent(
      condition: !isDesktop,
      builder: (child) {
        return Background(
          child: Scaffold(
            backgroundColor:
                Theme.of(context).extension<StackColors>()!.background,
            appBar: AppBar(
              leading: AppBarBackButton(
                onPressed: () async {
                  Navigator.of(context).pop();
                },
              ),
              title: FittedBox(
                fit: BoxFit.scaleDown,
                child: Text(
                  "Sync only selected wallets at startup",
                  style: STextStyles.navBarTitle(context),
                ),
              ),
            ),
            body: Padding(
              padding: const EdgeInsets.only(
                left: 12,
                top: 12,
                right: 12,
              ),
              child: child,
            ),
          ),
        );
      },
      child: ConditionalParent(
        condition: isDesktop,
        builder: (child) {
          return Padding(
            padding: EdgeInsets.symmetric(horizontal: 32),
            child: child,
          );
        },
        child: LayoutBuilder(builder: (context, constraints) {
          return SingleChildScrollView(
            child: ConstrainedBox(
              constraints: BoxConstraints(
                minHeight: constraints.maxHeight - 24,
              ),
              child: IntrinsicHeight(
                child: Padding(
                  padding: const EdgeInsets.all(4),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      const SizedBox(
                        height: 4,
                      ),
                      Text(
                        "Choose the wallets to sync automatically at startup",
                        style: STextStyles.smallMed12(context),
                      ),
                      const SizedBox(
                        height: 12,
                      ),
                      RoundedWhiteContainer(
                        padding: const EdgeInsets.all(0),
                        borderColor: !isDesktop
                            ? Colors.transparent
                            : Theme.of(context)
                                .extension<StackColors>()!
                                .background,
                        child: Column(
                          children: [
                            ...managers.map(
                              (manager) => Padding(
                                padding: const EdgeInsets.all(12),
                                child: Row(
                                  key: Key(
                                      "syncingPrefsSelectedWalletIdGroupKey_${manager.walletId}"),
                                  children: [
                                    Container(
                                      decoration: BoxDecoration(
                                        color: Theme.of(context)
                                            .extension<StackColors>()!
                                            .colorForCoin(manager.coin)
                                            .withOpacity(0.5),
                                        borderRadius: BorderRadius.circular(
                                          Constants.size.circularBorderRadius,
                                        ),
                                      ),
                                      child: Padding(
                                        padding: const EdgeInsets.all(4),
                                        child: SvgPicture.file(
                                          File(
                                            ref.watch(
                                              coinIconProvider(manager.coin),
                                            ),
                                          ),
                                          width: 20,
                                          height: 20,
                                        ),
                                      ),
                                    ),
                                    const SizedBox(
                                      width: 12,
                                    ),
                                    Column(
                                      mainAxisAlignment:
                                          MainAxisAlignment.spaceBetween,
                                      crossAxisAlignment:
                                          CrossAxisAlignment.start,
                                      children: [
                                        Text(
                                          manager.walletName,
                                          style:
                                              STextStyles.titleBold12(context),
                                        ),
                                        const SizedBox(
                                          height: 2,
                                        ),
                                        Text(
                                          ref
                                              .watch(pAmountFormatter(
                                                  manager.coin))
                                              .format(manager.balance.total),
                                          style:
                                              STextStyles.itemSubtitle(context),
                                        )
                                      ],
                                    ),
                                    const Spacer(),
                                    SizedBox(
                                      height: 20,
                                      width: 40,
                                      child: DraggableSwitchButton(
                                        isOn: ref
                                            .watch(prefsChangeNotifierProvider
                                                .select((value) => value
                                                    .walletIdsSyncOnStartup))
                                            .contains(manager.walletId),
                                        onValueChanged: (value) {
                                          final syncType = ref
                                              .read(prefsChangeNotifierProvider)
                                              .syncType;
                                          final ids = ref
                                              .read(prefsChangeNotifierProvider)
                                              .walletIdsSyncOnStartup
                                              .toList();
                                          if (value) {
                                            ids.add(manager.walletId);
                                          } else {
                                            ids.remove(manager.walletId);
                                          }

                                          switch (syncType) {
                                            case SyncingType.currentWalletOnly:
                                              if (manager.isActiveWallet) {
                                                manager.shouldAutoSync = value;
                                              }
                                              break;
                                            case SyncingType
                                                  .selectedWalletsAtStartup:
                                            case SyncingType
                                                  .allWalletsOnStartup:
                                              manager.shouldAutoSync = value;
                                              break;
                                          }

                                          ref
                                              .read(prefsChangeNotifierProvider)
                                              .walletIdsSyncOnStartup = ids;
                                        },
                                      ),
                                    ),
                                  ],
                                ),
                              ),
                            ),
                          ],
                        ),
                      ),
                    ],
                  ),
                ),
              ),
            ),
          );
        }),
      ),
    );
  }
}
