import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:stackfrost/providers/wallet/wallet_balance_toggle_state_provider.dart';
import 'package:stackfrost/themes/stack_colors.dart';
import 'package:stackfrost/utilities/assets.dart';
import 'package:stackfrost/utilities/constants.dart';
import 'package:stackfrost/utilities/enums/wallet_balance_toggle_state.dart';
import 'package:stackfrost/utilities/text_styles.dart';

class DesktopBalanceToggleButton extends ConsumerWidget {
  const DesktopBalanceToggleButton({
    Key? key,
    this.onPressed,
  }) : super(key: key);

  final VoidCallback? onPressed;

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    return SizedBox(
      height: 22,
      width: 80,
      child: MaterialButton(
        color: Theme.of(context).extension<StackColors>()!.buttonBackSecondary,
        splashColor: Theme.of(context).extension<StackColors>()!.highlight,
        onPressed: () {
          if (ref.read(walletBalanceToggleStateProvider.state).state ==
              WalletBalanceToggleState.available) {
            ref.read(walletBalanceToggleStateProvider.state).state =
                WalletBalanceToggleState.full;
          } else {
            ref.read(walletBalanceToggleStateProvider.state).state =
                WalletBalanceToggleState.available;
          }
          onPressed?.call();
        },
        elevation: 0,
        highlightElevation: 0,
        hoverElevation: 0,
        padding: EdgeInsets.zero,
        materialTapTargetSize: MaterialTapTargetSize.shrinkWrap,
        shape: RoundedRectangleBorder(
          borderRadius: BorderRadius.circular(
            Constants.size.circularBorderRadius,
          ),
        ),
        child: Center(
          child: FittedBox(
            child: Text(
              ref.watch(walletBalanceToggleStateProvider.state).state ==
                      WalletBalanceToggleState.available
                  ? "AVAILABLE"
                  : "FULL",
              style: STextStyles.w500_10(context),
            ),
          ),
        ),
      ),
    );
  }
}

